mapper.select:
		select `department_id`,`company_id`,
		(select company_name from sys_company where sys_company.company_id = sys_department.company_id) as company_name,
		`department_name`,`time_id`,
		(select remark from sys_worktime where sys_worktime.work_id = sys_department.time_id) as time_remark,
		`province_id`,
		(select name from sys_region where sys_region.id = sys_department.province_id) as province_name,
		`city_id`,
		(select name from sys_region where sys_region.id = sys_department.city_id) as city_name,
		`status`,`address`,`telephone`,`home_page`,`remarks`,`create_user`,`modified_user`,`create_time`,`modified_time` from sys_department

		  		   and department_id = #{departmentId} 
		  		   and company_id = #{companyId} 
		  		   and department_name = #{departmentName} 
		  		   and time_id = #{timeId} 
		  		   and province_id = #{provinceId} 
		  		   and city_id = #{cityId} 
		  		   and status = #{status} 
		  		   and address = #{address} 
		  		   and telephone = #{telephone} 
		  		   and home_page = #{homePage} 
		  		   and remarks = #{remarks} 
		  		   and create_user = #{createUser} 
		  		   and modified_user = #{modifiedUser} 
		  		   and create_time = #{createTime} 
		  		   and modified_time = #{modifiedTime} 
		  		   not exists (select 1 from sys_worktime a where DAYOFWEEK(#{bookDate})  in (a.work_time) and a.work_id = sys_department.time_id)  



                order by ${sort} ${order}


                order by department_id desc



			limit #{offset}, #{limit}

select count(*) from sys_department

  		   and department_id = #{departmentId} 
  		   and company_id = #{companyId} 
  		   and department_name = #{departmentName} 
  		   and time_id = #{timeId} 
  		   and province_id = #{provinceId} 
  		   and city_id = #{cityId} 
  		   and status = #{status} 
  		   and address = #{address} 
  		   and telephone = #{telephone} 
  		   and home_page = #{homePage} 
  		   and remarks = #{remarks} 
  		   and create_user = #{createUser} 
  		   and modified_user = #{modifiedUser} 
  		   and create_time = #{createTime} 
  		   and modified_time = #{modifiedTime}
