mapper.select:
		select a.`id`,a.`ad_position_id`,a.`media_type`,a.`name`,a.`link`,a.`image_url`,a.`content`,a.`end_time`,a.`enabled`,d.name as ad_position_name,d.width as width ,d.height as height  
		from ad_info a,ad_position d 

         a.ad_position_id = d.id 
         and a.end_time >= now() 
		  		   and a.id = #{id} 
		  		   and a.ad_position_id = #{adPositionId} 
		  		   and a.media_type = #{mediaType} 
		  		   and a.name = #{name} 
		  		   and a.link = #{link} 
		  		   and a.image_url = #{imageUrl} 
		  		   and a.content = #{content} 
		  		   and a.end_time = #{endTime} 
		  		   and a.enabled = #{enabled} 



                order by ${sort} ${order}


                order by a.id desc



			limit #{offset}, #{limit}

select count(*) from ad_info

  		   and id = #{id} 
  		   and ad_position_id = #{adPositionId} 
  		   and media_type = #{mediaType} 
  		   and name = #{name} 
  		   and link = #{link} 
  		   and image_url = #{imageUrl} 
  		   and content = #{content} 
  		   and end_time = #{endTime} 
  		   and enabled = #{enabled}
